SELECT stu.*, banji.class_name as banji_name, bus_base_info.number as bus_number, bus_stations.station_name AS board_station_morning_name, bs_m.station_name as board_station_afternoon_name FROM student AS stu LEFT JOIN banji ON banji.id = stu.banji LEFT JOIN bus_line ON bus_line.id = stu.bus_line_afternoon LEFT JOIN bus_base_info ON bus_base_info.id = bus_line.bus_base_info LEFT JOIN bus_stations ON bus_stations.id = stu.board_station_afternoon LEFT JOIN bus_stations as bs_m ON bs_m.id = stu.board_station_morning WHERE stu.valid = 1
AND banji.grade = #{gradeName}
AND banji.class_name = #{className}
AND (stu.student_number LIKE CONCAT('%', '${queryKey}', '%') OR stu.name LIKE CONCAT('%', '${queryKey}', '%') OR bus_stations.station_name LIKE CONCAT('%', '${queryKey}', '%') OR bus_base_info.number LIKE CONCAT('%', '${queryKey}', '%'))

SELECT student.*, banji.class_name as banji_name, bus_base_info.number as bus_number, bus_stations.station_name AS board_station_morning_name, bs_a.station_name AS board_station_afternoon_name FROM student LEFT JOIN banji ON banji.id = student.banji LEFT JOIN bus_line ON bus_line.id = student.bus_line_morning LEFT JOIN bus_base_info ON bus_base_info.id = bus_line.bus_base_info LEFT JOIN bus_stations ON bus_stations.id = student.board_station_morning LEFT JOIN bus_stations AS bs_a ON bs_a.id = student.board_station_afternoon WHERE student.valid = 1
AND bus_base_info.number = #{busNumber}
AND bus_stations.station_name = #{busStation}
AND banji.grade = #{gradeName}
AND banji.class_name = #{className}
AND bus_line.`mode` = '上学'

SELECT student.*, banji.class_name as banji_name, bus_base_info.number as bus_number, bus_stations.station_name AS board_station_afternoon_name, bs_m.station_name AS board_station_morning_name FROM student LEFT JOIN banji ON banji.id = student.banji LEFT JOIN bus_line ON bus_line.id = student.bus_line_afternoon LEFT JOIN bus_base_info ON bus_base_info.id = bus_line.bus_base_info LEFT JOIN bus_stations ON bus_stations.id = student.board_station_afternoon LEFT JOIN bus_stations AS bs_m ON bs_m.id = student.board_station_morning WHERE student.valid = 1
AND bus_base_info.number = #{busNumber}
AND bus_stations.station_name = #{busStation}
AND banji.grade = #{gradeName}
AND banji.class_name = #{className}
AND bus_line.`mode` = '放学'

SELECT student.*, banji.class_name AS banji_name, bus_base_info.number AS bus_number, bs_a.station_name AS board_station_afternoon_name, bs_m.station_name AS board_station_morning_name FROM student LEFT JOIN banji ON banji.id = student.banji LEFT JOIN bus_line as bl_a ON bl_a.id = student.bus_line_afternoon -- 早班午班同一辆车 LEFT JOIN bus_base_info ON bus_base_info.id = bl_a.bus_base_info -- 早班午班同一辆车
LEFT JOIN bus_stations as bs_a ON bs_a.id = student.board_station_afternoon -- 早班午班同一站 LEFT JOIN bus_stations as bs_m ON bs_m.id = student.board_station_morning -- 早班午班同一站 WHERE student.valid = 1
AND bus_base_info.number = #{busNumber}
AND bus_stations.station_name = #{busStation}
AND banji.grade = #{gradeName}
AND banji.class_name = #{className}

SELECT student.*, banji.class_name AS banji_name, bus_base_info.number AS bus_number, bs_a.station_name AS board_station_afternoon_name, bs_m.station_name AS board_station_morning_name FROM student LEFT JOIN banji ON banji.id = student.banji LEFT JOIN bus_line as bl_a ON bl_a.id = student.bus_line_afternoon -- 早班午班同一辆车 LEFT JOIN bus_base_info ON bus_base_info.id = bl_a.bus_base_info -- 早班午班同一辆车
LEFT JOIN bus_stations as bs_a ON bs_a.id = student.board_station_afternoon -- 早班午班同一站 LEFT JOIN bus_stations as bs_m ON bs_m.id = student.board_station_morning -- 早班午班同一站 WHERE student.valid = 1
AND (student.student_number LIKE CONCAT('%', '${queryKey}', '%') OR student.name LIKE CONCAT('%', '${queryKey}', '%') OR bs_a.station_name LIKE CONCAT('%', '${queryKey}', '%') OR bus_base_info.number LIKE CONCAT('%', '${queryKey}', '%'))
AND banji.grade = #{gradeName}
AND banji.class_name = #{className}
