mapper.select:
SELECT tr.*, bbi.number as bus_number , bus_stations_m.station_name as board_station_name_morning, bus_stations_a.station_name as board_station_name_afternoon, bus_stations_c.station_name as current_station_name, psi.board_time, banji.grade as student_grade, banji.class_name as student_banji, student.student_number, student.`name` as student_name, bus_line.`mode`, bus_line.stations as bus_line_stations, bus_line.`name` as bus_line_name FROM transport_record AS tr LEFT JOIN bus_line on tr.bus_line = bus_line.id LEFT JOIN bus_base_info as bbi on bus_line.bus_base_info=bbi.id LEFT JOIN picked_students_info as psi on psi.transport_record_id = tr.id LEFT JOIN student on psi.student_id = student.id LEFT JOIN bus_stations as bus_stations_m on bus_stations_m.id = student.board_station_morning LEFT JOIN bus_stations as bus_stations_a on bus_stations_a.id = student.board_station_afternoon LEFT JOIN bus_stations as bus_stations_c on bus_stations_c.id = tr.current_station LEFT JOIN banji on banji.id = student.banji WHERE 1 = 1
AND DATE_FORMAT( psi.board_time, '%Y-%m-%d %T' ) >= #{queryStartTime}
AND DATE_FORMAT( psi.board_time, '%Y-%m-%d %T' ) <= #{queryFinishTime}
AND student.`name` LIKE CONCAT('%', '${studentName}', '%')
AND student.student_number = #{studentNumber}
AND bbi.number = #{busNumber}
AND bus_line.`mode` = #{busMode}
AND ( bus_stations_a.station_name = #{busStationName} and bus_stations_m.station_name = #{busStationName} )
AND banji.grade = #{grade}
AND banji.class_name = #{className}
AND tr.flag = #{recordFlag}
AND tr.`status` = #{recordStatus}
ORDER BY tr.begin_time desc

    SELECT
	*
    FROM
        transport_record AS tr
    LEFT JOIN bus_line AS bl ON bl.id = tr.bus_line
    LEFT JOIN bus_base_info AS bbi ON bbi.id = bl.bus_base_info
    WHERE  1=1
    
      AND tr.bus_number_in_tr = #{busNumber}
    
    
      AND bl.`mode` = #{busMode}
    

    
      AND DATE_FORMAT( tr.date,  '%Y-%m-%d' ) = #{queryDate}
    

    
      and bl.`name` = #{busLineName}
